insert into Recipe (id, name) values (1, 'Bacalhau a bras');
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1', 'cebola', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('2 dentes', 'alho', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('4 c/sopa ', 'azeite', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('600g', 'bacalhau demolhado e desfiado', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('200g', 'batata palha', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('6 ', 'ovos M', 1);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('qb ', 'salsa picada', 1);
insert into recipe_steps (text, r_steps) values ('Numa frigideira larga em lume brando, salteie a cebola às rodelas e os dentes de alho esmagados no azeite.', 1);
insert into recipe_steps (text, r_steps) values ('Acrescente o bacalhau e as batatas-palha e misture bem. Deixe as batatas amolecerem um pouco.', 1);
insert into recipe_steps (text, r_steps) values ('Numa tigela, bata ligeiramente os ovos com um garfo.', 1);
insert into recipe_steps (text, r_steps) values ('Acrescente os ovos ao bacalhau e misture até estar bem ligado e os ovos estarem cozinhados.', 1);
insert into recipe_steps (text, r_steps) values ('Prove e afine os temperos. Sirva o bacalhau à Brás, salpicado com a salsa e as azeitonas.', 1);
insert into Recipe (id, name) values (2, 'Carbonara');
insert into recipe_ingredients (quantity, name, r_ingredients) values ('3', 'Eggs', 2);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('40g', 'Parmesan cheese', 2);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('150g', 'Pancetta', 2);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('200g', 'Dried spaghetti', 2);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('qb', 'extra virgin olive oil', 2);
insert into recipe_steps (text, r_steps) values ('Put the egg yolks into a bowl, finely grate in the Parmesan, season with pepper, then mix well with a fork and put to one side.', 2);
insert into recipe_steps (text, r_steps) values ('Cut any hard skin off the pancetta and set aside, then chop the meat.', 2);
insert into recipe_steps (text, r_steps) values ('Cook the spaghetti in a large pan of boiling salted water until al dente.', 2);
insert into recipe_steps (text, r_steps) values ('Meanwhile, rub the pancetta skin, if you have any, all over the base of a medium frying pan (this will add fantastic flavour, or use 1 tablespoon of oil instead), then place over a medium-high heat.', 2);
insert into recipe_steps (text, r_steps) values ('Peel the garlic, then crush with the palm of your hand, add it to the pan and leave it to flavour the fat for 1 minute. Stir in the pancetta, then cook for 4 minutes, or until it starts to crisp up.', 2);
insert into recipe_steps (text, r_steps) values ('Pick out and discard the garlic from the pan, then, reserving some of the cooking water, drain and add the spaghetti. Toss well over the heat so it really soaks up all that lovely flavour, then remove the pan from the heat.', 2);
insert into recipe_steps (text, r_steps) values ('Add a splash of the cooking water and toss well, season with pepper, then pour in the egg mixture – the pan will help to cook the egg gently, rather than scrambling it. Toss well, adding more cooking water until it’s lovely and glossy.', 2);
insert into recipe_steps (text, r_steps) values ('Serve with a grating of Parmesan and an extra twist of pepper.', 2);
insert into Recipe (id, name) values (3, 'Cauliflower fritters');
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1 head', 'cauliflower (cut into florets)', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('2 tbsp', 'olive oil', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1 stp', 'salt', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('3/4 cup', 'bread crumbs', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('2 large', 'eggs', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('2 tbsp', 'cornstarch (or flour)', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('2 tbsp', 'greek yogurt', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1', 'garlic clove (minced)', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1 and 1/2 tsp', 'curry powder', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1 tsp', 'ground cumin', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('1/2 tsp', 'turmeric', 3);
insert into recipe_ingredients (quantity, name, r_ingredients) values ('qb', 'vegetable oil', 3);
insert into recipe_steps (text, r_steps) values ('Preheat oven to 400°F (200°C) and place a rack in the middle.', 3);
insert into recipe_steps (text, r_steps) values ('Place cauliflower florets on a large baking sheet and drizzle with olive oil, sprinkle with salt, and toss so that it’s evenly coated.', 3);
insert into recipe_steps (text, r_steps) values ('Bake for 20 minutes, turning once at halfway to ensure even roasting. Remove from the oven and transfer to the bowl of a food processor. Pulse until completely chopped.', 3);
insert into recipe_steps (text, r_steps) values ('Transfer chopped cauliflower to a large bowl and add breadcrumbs, eggs, flour, Greek yogurt, spices, and ¼ teaspoon of salt. Mix thoroughly until combined. Cover and refrigerate for at least 1 hour.', 3);
insert into recipe_steps (text, r_steps) values ('If the mixture is too "sloppy", add more breadcrumbs or cornstarch/flour. You should be able to easily shape the fritters without making a sticky mess.', 3);
insert into recipe_steps (text, r_steps) values ('Scoop 2 heaping tablespoons of cauliflower mixture and with dampened hands form into patties about ¾-inch thick.', 3);
insert into recipe_steps (text, r_steps) values ('Place a skillet over medium-high heat, pour in vegetable oil until it reaches ½-inch up the sides.', 3);
insert into recipe_steps (text, r_steps) values ('Once the oil is hot fry patties in batches for about 2 minutes per side, until golden brown.', 3);
insert into recipe_steps (text, r_steps) values ('Transfer to a plate lined with paper towels to drain. Keep warm in the oven while frying the remaining patties.', 3);
